import React, { Component } from 'react';
import './ToggleModeButton.css';


class ToggleModeButton extends Component {

  constructor(props) {
    super(props);
    this.state = {isDarkMode: false}
  }

  componentDidMount() {
	const $button = document.getElementById('toggleModeButton');
	$button.setAttribute('aria-pressed', window.matchMedia('prefers-color-scheme: dark') ? this.setState({isDarkMode: true}) : this.setState({isDarkMode: false}));
  }  


  toggle = () => {
	const $button = document.getElementById('toggleModeButton');
	$button.setAttribute('aria-pressed', $button.matches('[aria-pressed="true"]') ? this.setState({isDarkMode: false}) : this.setState({isDarkMode: true}));
  }

  render() {
    return (
	  <button id="toggleModeButton" aria-pressed="{this.isDarkMode}" onClick={this.toggle}>
		<span class="sr-only">Toggle Dark Mode</span>
		<span class="sun-and-moon">
		  <span class="rays">
			<span class="ray" style="--index: 0; --entry: 0; --exit: 1;"></span>
			<span class="ray" style="--index: 1; --entry: 1; --exit: 2;"></span>
			<span class="ray" style="--index: 2; --entry: 0; --exit: 3;"></span>
			<span class="ray" style="--index: 3; --entry: 1; --exit: 4;"></span>
			<span class="ray" style="--index: 4; --entry: 0; --exit: 3;"></span>
			<span class="ray" style="--index: 5; --entry: 1; --exit: 2;"></span>
			<span class="ray" style="--index: 6; --entry: 0; --exit: 1;"></span>
			<span class="ray" style="--index: 7; --entry: 1; --exit: 0;"></span>
		  </span>
		  <span class="star" style="--index: 0;"></span>
		  <span class="star" style="--index: 1;"></span>
		  <span class="body"></span>
		</span>
	  </button>
    );
  }
}

export default ToggleModeButton;
