import React, { Component } from 'react';
import './App.css';
import USAMap from "react-usa-map";
import axios from 'axios';
import * as XLSX from 'xlsx';
import ExcelReader from './components/StateInfo';'./components/StateInfo.js';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {currentStateData: 'Select a state'}
  }

  handleFetchFromUrl = async (event) => {
    this.a = "Loading";
    const stateName = event.target.querySelector('title').innerHTML;
    const url = '/state_M2023_dl.xlsx';
    console.log(url);

    try {
      const response = await axios.get(url, { responseType: 'arraybuffer' });

      const data = new Uint8Array(response.data);
      const workbook = XLSX.read(data, { type: 'array' });
      const sheetName = workbook.SheetNames[0];
      const sheet = workbook.Sheets[sheetName];
      const jsonData = XLSX.utils.sheet_to_json(sheet, { header: 32 });
      const currentStateDataArray = jsonData.filter(i => i.AREA_TITLE === stateName);

      this.state.currentStateData = JSON.stringify(currentStateDataArray);
    } catch (error) {
      console.error('Error fetching or parsing Excel file:', error);
    }
  };

  mapHandler = (event) => {
    let stateName = event.target.querySelector('title').innerHTML;
  };

  statesFill = () => {
    return {
      "CA": {
        fill: "rgba(0,0,155,0.8)",
        clickHandler: () => alert("Custom callback for this US state.")
      }
    };
  };

  render() {
    return (
      <div className="App">
        <h1 className="header">Mike's US Labor Data Visualization</h1>
        <USAMap defaultFill={"rgba(250,255,250,0.8)"} customize={this.statesFill()} onClick={this.mapHandler} onMouseOver={this.handleFetchFromUrl} />
        <p>{this.state.currentStateData}</p>
      </div>
    );
  }
}

export default App;
